(function () {
    'use strict';

    angular
        .module('myShopApp')
        .service('APIInterceptorService', APIInterceptorService);

    APIInterceptorService.$inject = [];

    function APIInterceptorService() {
        this.request = request;
        this.response = response;
        this.requestError = requestError;
        this.responseError = responseError;

        ////////////////

        function request(config) {
            //TODO : Auth service to get authorization
            return config;
        }

        function response(config) {
            return config;
        }

        function requestError(response) {
            return response;
        }

        function responseError(response) {
            
            return response;
        }
    }
}());

(function () {
    'use strict';

    angular
        .module('myShopApp')
        .service('dataAccessService', DataAccessService);

    DataAccessService.$inject = ["$http", "$q"];

    function DataAccessService($http, $q) {
        this.fetch = get;
        this.feed = post;
        this.update = put;
        this.remove = remove;
        ////////////////
        var deferer;

        function error(error) {
            deferer.reject(error);
            console.error("In Data-Service Error-Callback :",error);            
        }

        function success(response) {
            deferer.resolve(response.data);
            console.info("In Data-Service Success-Callback :",response);
        }

        function get(url) {
            deferer = $q.defer();
            $http.get(url,{cache:false}).then(success, error);
            return deferer.promise;
        }

        function post(url,data) {
            //deferer = $q.defer();
           return $http.post(url,{payLoad:data});//.then(success,error);
            //return deferer.promise;
        }

        function put() {}

        function remove() {}
    }
}());

(function () {
    'use strict';

    angular
        .module('myShopApp')
        .directive('headerBreadcrum', headerBreadcrumDirective);

    headerBreadcrumDirective.$inject = [];

    function headerBreadcrumDirective() {
        // Usage:
        //
        // Creates:a Title for the current page and bread crum
        //
        var directive = {
            template: `<div class="row">        
                    <div class="col-lg-12">
                        <h1 class="page-header">
                            {{vm.headerBig}} <small>{{vm.headerSmall}}</small>
                        </h1>
                        <ol class="breadcrumb">
                            <li class="active">
                                <i class="fa {{vm.iconClass}}"></i> {{vm.headerBig}}
                            </li>
                        </ol>
                    </div>
                </div>`,
            bindToController: true,
            controller: HearderBreadCrumController,
            controllerAs: 'vm',
            restrict: 'E',
            scope: {
                headerBig: "@",
                headerSmall: "@",
                iconClass: "@"
            }
        };
        return directive;
    }
    /* @ngInject */
    function HearderBreadCrumController() {
        var vm = this;
    }
}());

(function () {
    'use strict';

    angular
        .module('myShopApp')
        .controller('NavigationController', NavigationController);

    NavigationController.inject = ['$rootScope', '$location'];

    function NavigationController($rootScope, $location) {
        var vm = this;
        activate();

        function activate() {
            selectCurrentSideMenu();
        }

        function selectCurrentSideMenu() {
            let moduleName= $location.$$path.split('/')[2];
            vm.selectedSideMenu = moduleName ? moduleName.toLowerCase():"";
        }
        // $rootScope.$on('$stateChangeStart',
        //     function (event, toState, toParams, fromState, fromParams, options) {
        //         vm.selectedSideMenu = $location.$$path.split('/')[2];
        //     });
        $rootScope.$on('$stateChangeSuccess',
            function (event, toState, toParams, fromState, fromParams, options) {
                selectCurrentSideMenu();
            });
    }
}());

