(function () {
    'use strict';

    angular
        .module('myShopApp')
        .controller('AddProductController', AddProductController);

    AddProductController.$inject = ["$state", "dataAccessService"];

    function AddProductController($state, dataAccessService) {
        var vm = this;
        vm.product = {
            brand: "",
            name: "",
            vendorName: ""
        };
        vm.saveProduct = saveProduct;

        function saveProduct() {
            dataAccessService.feed("/api/post/add/product", vm.product)
                .then(function (response) {
                    $state.go("main.products.list");
                }, function (error) {
                    console.error(error);
                })
        }
    }
}());

(function () {
    'use strict';

    angular
        .module('myShopApp')
        .controller('ProductEditController', ProductEditController);

    ProductEditController.$inject = ["dataAccessService", "productItem"];

    function ProductEditController(dataAccessService, productItem) {
        var vm = this;
        vm.product = productItem;
        vm.updateProduct = updateProduct;

        function updateProduct() {
            dataAccessService.update("/api/post/update/product", vm.product)
                .then(function (response) {
                     $state.go("products.lists");
                }, function (error) {
                    console.error(error);
                })
        }
    }
}());

(function () {
    'use strict';

    angular
        .module('myShopApp')
        .controller('ProductListController', ProductListController);

    ProductListController.$inject = ['$location', 'dataAccessService'];

    function ProductListController($location, dataAccessService) {
        var vm = this;
        activate();

        vm.editProduct = editProduct;

        function editProduct(id) {
            //TODO : redirect to Edit Controller
            $location.path('/main/products/edit/' + id);
        }

        function activate() {
            dataAccessService.fetch("/api/get/list/product")
                .then(function (products) {
                    vm.products = products;
                });
        }
    }
}());